mapper.select:
select l.id as id, l.login_ip as loginIp, date_format(l.login_time,'%Y-%m-%d %H:%i:%s') as time, b.sys_acc_name as accName, b.sys_acc_realname as realName, b.t_sys_role_name as accType from login_log l, (select a.*,r.t_sys_role_name from sys_acc a,sysrole r where a.sys_acc_roleid = r.t_sys_role_id) b where l.acc_id = b.sys_acc_id
and b.sys_acc_name = #{accName}
order by l.id desc
limit #{page} , #{pageCount}

select 
	count(l.id)
from login_log l,
(select a.*,r.t_sys_role_name from sys_acc a,sysrole r where a.sys_acc_roleid = r.t_sys_role_id) b
where l.acc_id = b.sys_acc_id 

	and b.sys_acc_name = #{accName}
