mapper.select:
select l.id as id, l.operate_type as operateType, l.operate_des as operateDes, date_format(l.operate_time,'%Y-%m-%d %H:%i:%s') as operateTime, b.sys_acc_name as accName, b.sys_acc_realname as realName, b.t_sys_role_name as accType from operate_log l, (select a.*,r.t_sys_role_name from sys_acc a,sysrole r where a.sys_acc_roleid = r.t_sys_role_id) b where l.acc_id = b.sys_acc_id
and b.sys_acc_name = #{accName}
date_format(l.operate_time,'%Y-%m-%d') > date_format(#{beginTime},'%Y-%m-%d')
date_format(l.operate_time,'%Y-%m-%d') < date_format(#{endTime},'%Y-%m-%d')
operate_type = #{operateType}
order by l.id desc
limit #{page} , #{pageCount}
